sku_name skuName,sku_count skuCount,sku_price skuPrice,status,create_time createTime,update_time updateTime
FROM order_shop


        AND username =#{username}


        AND order_id =#{orderId}

SELECT a.order_id orderId,a.order_num orderNum,b.sku_id skuId,b.sku_name skuName,
b.sku_count skuCount,b.sku_price skuStorePrice,b.sku_status skuStatus,a.order_buyer_remark orderBuyerRemark
FROM order_jd a
JOIN order_shop b ON b.order_id=a.order_id AND b.username = #{username}
WHERE a.produce_station_no = #{stationNo} AND b.order_status = #{orderStatus}

SELECT sku_id skuId,`status` FROM order_shop
WHERE sku_id IN

    #{temp}

